// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract Staking is Ownable {
    IERC20 public stakingToken;
    uint256 public rewardRate; // Reward rate in tokens per second
    uint256 public penaltyRate; // Penalty rate for early withdrawal (in basis points, e.g., 1000 = 10%)
    uint256 public stakingDuration; // Minimum staking duration in seconds

    struct Stake {
        uint256 amount;
        uint256 startTime;
    }

    mapping(address => Stake) public userStakes;
    mapping(address => uint256) public userRewards;
    uint256 private _totalSupply;

    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount, uint256 penalty);
    event RewardPaid(address indexed user, uint256 reward);

    constructor (
        address _stakingToken,
        uint256 _rewardRate,
        uint256 _penaltyRate,
        uint256 _stakingDuration
    )Ownable(msg.sender) {
        stakingToken = IERC20(_stakingToken);
        rewardRate = _rewardRate;
        penaltyRate = _penaltyRate;
        stakingDuration = _stakingDuration;
    }

    function stake(uint256 _amount) external {
        require(_amount > 0, "Cannot stake 0");
        stakingToken.transferFrom(msg.sender, address(this), _amount);
        userStakes[msg.sender] = Stake(_amount, block.timestamp);
        _totalSupply += _amount;
        emit Staked(msg.sender, _amount);
    }

    function withdraw(uint256 _amount) public {
        require(_amount > 0, "Cannot withdraw 0");
        Stake storage stakeInfo = userStakes[msg.sender];
        require(_amount <= stakeInfo.amount, "Cannot withdraw more than staked");

        uint256 penalty = 0;
        if (block.timestamp < stakeInfo.startTime + stakingDuration) {
            penalty = (_amount * penaltyRate) / 10000; // Penalty calculation
            stakingToken.transfer(owner(), penalty);
        }

        uint256 amountAfterPenalty = _amount - penalty;
        stakeInfo.amount -= _amount;
        _totalSupply -= _amount;
        stakingToken.transfer(msg.sender, amountAfterPenalty);
        emit Withdrawn(msg.sender, amountAfterPenalty, penalty);
    }

    function calculateReward(address _account) public view returns (uint256) {
        Stake storage stakeInfo = userStakes[_account];
        if (stakeInfo.amount == 0) return 0;
        uint256 stakingTime = block.timestamp - stakeInfo.startTime;
        uint256 reward = (stakingTime * rewardRate * stakeInfo.amount) / 1e18;
        return reward;
    }

    function getReward() public {
        uint256 reward = calculateReward(msg.sender);
        require(reward > 0, "No rewards available");
        userRewards[msg.sender] += reward;
        stakingToken.transfer(msg.sender, reward);
        emit RewardPaid(msg.sender, reward);
    }

    function exit() external {
        withdraw(userStakes[msg.sender].amount);
        getReward();
    }

    function totalSupply() external view returns (uint256) {
        return _totalSupply;
    }
}

